import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { listPostDetails } from '../redux/actions/post';
import { Link } from 'react-router-dom';
import ReactLoading from 'react-loading';
import FormContainer from '../components/FormContainer';
import { Col, Row, ListGroup, Card, Button, Form } from 'react-bootstrap';

const PostScreen = ({ match }) => {
  const [user, setUser] = useState('');
  const [comment, setComment] = useState('');
  const [commentVisible, setCommentVisible] = useState(false);
  const dispatch = useDispatch();
  const postDetails = useSelector((state) => state.postDetails);
  const { loading, post, error } = postDetails;

  const addCommentHandler = () => {
    setCommentVisible(true);
  };

  useEffect(() => {
    dispatch(listPostDetails(match.params.id));
  }, [dispatch, match]);

  return (
    <>
      <Link className='btn btn-dark my-3' to='/'>
        Go Back
      </Link>

      {loading ? (
        <ReactLoading
          className='m-auto'
          type='spin'
          color='green'
          height={'10%'}
          width={'10%'}
        />
      ) : error ? (
        <h1>error</h1>
      ) : (
        <Row>
          <Col md={6}>
            <ListGroup variant='flush'>
              <ListGroup.Item>
                <h3>{post.title}</h3>
              </ListGroup.Item>

              <ListGroup.Item>Name : {post.name}</ListGroup.Item>
              <ListGroup.Item>
                Description : {post.content}
                excepturi.
              </ListGroup.Item>
            </ListGroup>
          </Col>
          <Col md={6}>
            <ListGroup>
              <ListGroup.Item className='bg-dark text-light'>
                <h5>Comments</h5>
              </ListGroup.Item>
              <ListGroup.Item>
                <Row>
                  <Col md={3}>
                    <i className='fas fa-user-circle'> prathmesh kamble</i>
                  </Col>
                  <Col md={9}>
                    <p>
                      Lorem ipsum dolor sit amet consectetur adipisicing elit.
                      Officia quis aspernatur optio blanditiis, velit, est nobis
                      saepe in, aut ut fuga nihil praesentium expedita
                      voluptatibus. Ut, debitis. Nam, voluptatem est facere
                      fugit dolore amet. Illum ipsum excepturi blanditiis cum
                      necessitatibus molestias perferendis porro eos quis, vel
                      sapiente alias ducimus esse?
                    </p>
                  </Col>
                </Row>
              </ListGroup.Item>
              <ListGroup.Item>
                <Button
                  onClick={addCommentHandler}
                  className='btn btn-md btn-success'
                  type='button'
                >
                  Add a Comment
                </Button>
              </ListGroup.Item>
              // Handle comment button clicked
              {commentVisible && (
                <FormContainer>
                  <Form onSubmit={submitCommentHandler}>
                    <Form.Group controlId='name'>
                      <Form.Label>Name </Form.Label>
                      <Form.Control
                        type='text'
                        placeholder='enter your name'
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        style={{ borderLeft: '5px solid green' }}
                      ></Form.Control>
                    </Form.Group>

                    <Form.Group controlId='content'>
                      <Form.Label>Content </Form.Label>
                      <Form.Control
                        placeholder='enter content'
                        as='textarea'
                        rows={5}
                        value={content}
                        onChange={(e) => setContent(e.target.value)}
                        style={{ borderLeft: '5px solid green' }}
                      ></Form.Control>
                    </Form.Group>

                    <Button
                      type='submit'
                      className='btn btn-block btn-success py-2'
                    >
                      <i class='fas fa-thumbs-up'></i> Submit
                    </Button>
                  </Form>
                </FormContainer>
              )}
            </ListGroup>
          </Col>
        </Row>
      )}
    </>
  );
};

export default PostScreen;
