import React from 'react';
import { Link } from 'react-router-dom';
import { Card, Row, Col } from 'react-bootstrap';
import { LinkContainer } from 'react-router-bootstrap';
const Post = ({ post }) => {
  const convertDate = (input) => {
    const date = new Date(input);
    return date.toDateString();
  };

  return (
    <>
      <LinkContainer to={`/posts/${post._id}`}>
        <Row>
          <Col>
            <Card className=' shadow rounded text-center'>
              <Card.Body>
                <Card.Header className='bg-info text-warning' as='h4'>
                  {post.title}
                </Card.Header>

                <hr />
                <Card.Text as='h5'>{post.name}</Card.Text>
                <hr />
                <Card.Text as='p'>{post.content}</Card.Text>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </LinkContainer>
      <Row>
        <Col className='my-2'>
          <p className='text-danger '>{convertDate(post.createdAt)}</p>
        </Col>
      </Row>
    </>
  );
};

export default Post;
