import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { listPostDetails } from '../redux/actions/post';
import { Link } from 'react-router-dom';
import {
  Col,
  Row,
  Image,
  ListGroup,
  Card,
  Button,
  Form,
  Container,
} from 'react-bootstrap';
// import Rating from '../components/Rating';
// import Message from '../components/Message';
// import Loader from '../components/Loader';

const PostScreen = ({ match }) => {
  const dispatch = useDispatch();
  const postDetails = useSelector((state) => state.postDetails);
  const { loading, post, error } = postDetails;

  const addCommentHandler = () => {};

  useEffect(() => {
    dispatch(listPostDetails(match.params.id));
  }, [dispatch, match]);

  return (
    <>
      <Link className='btn btn-dark my-3' to='/'>
        Go Back
      </Link>

      {/* {loading ? (
        <Loader />
      ) : error ? (
        <Message variant='danger'>Product not found</Message>
      ) : ( */}
      <Row>
        <Col md={6} className='shadow'>
          <Image
            src='https://storage.ning.com/topology/rest/1.0/file/get/8667507462?profile=RESIZE_710x'
            alt=''
            fluid
          />
        </Col>

        <Col md={6}>
          <ListGroup variant='flush'>
            <ListGroup.Item>
              <h3>{post.title}</h3>
            </ListGroup.Item>

            <ListGroup.Item>Name : {post.name}</ListGroup.Item>
            <ListGroup.Item>
              Description : Lorem, ipsum dolor sit amet consectetur adipisicing
              elit. Quo, ipsa fuga. Expedita quo dicta maiores enim molestias,
              esse, distinctio sint fugit quam unde molestiae, omnis praesentium
              officia. Ipsam esse officia vitae harum rem recusandae in nihil,
              voluptatum autem! Nobis blanditiis alias cumque praesentium
              fugiat? Iure saepe illo doloribus vel excepturi.
            </ListGroup.Item>
          </ListGroup>
        </Col>
      </Row>

      <Container>
        <Row>
          <Col>
            <ListGroup>
              <ListGroup.Item className='bg-dark text-light'>
                <i class='fas fa-user-circle'> Name</i>
              </ListGroup.Item>
              <ListGroup.Item className='bg-light'>
                <Row>
                  <Col>
                    Comment : lorem ipsum dolor sit amet, consectetur adip
                  </Col>
                </Row>
              </ListGroup.Item>

              <ListGroup.Item>
                <Button
                  onClick={addCommentHandler}
                  className='btn-block'
                  type='button'
                  // disabled={product.countInStock === 0}
                >
                  Add a Comment
                </Button>
              </ListGroup.Item>
            </ListGroup>
          </Col>
        </Row>
      </Container>

      {/* )} */}
    </>
  );
};

export default PostScreen;
