import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Form, Button, Row, Col } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import FormContainer from '../components/FormContainer';
import { createPost } from '../redux/actions/post';
import { POST_CREATE_RESET } from '../redux/constants/posts';

const CreatePostScreen = ({ location, history }) => {
  const [name, setName] = useState('');
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [message, setMessage] = useState();

  const dispatch = useDispatch();

  const postCreate = useSelector((state) => state.postCreate);
  const { error, loading, post } = postCreate;

  const submitHandler = (e) => {
    e.preventDefault();

    if (!name || !title || !content) {
      setMessage('All fields are required');
    } else {
      // Dispatch Register
      dispatch(createPost(title, name, content));
      history.push('/');
    }
  };

  return (
    <FormContainer>
      <h2 className='text-center text-warning my-2'>
        <i className='fas fa-pencil-alt text-success'></i> New Post
      </h2>

      <Form onSubmit={submitHandler}>
        <Form.Group controlId='title'>
          <Form.Label>Title </Form.Label>
          <Form.Control
            type='text'
            placeholder='enter title'
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            style={{ borderLeft: '5px solid green' }}
          ></Form.Control>
        </Form.Group>

        <Form.Group controlId='name'>
          <Form.Label>Name </Form.Label>
          <Form.Control
            type='text'
            placeholder='enter your name'
            value={name}
            onChange={(e) => setName(e.target.value)}
            style={{ borderLeft: '5px solid green' }}
          ></Form.Control>
        </Form.Group>

        <Form.Group controlId='content'>
          <Form.Label>Content </Form.Label>
          <Form.Control
            placeholder='enter content'
            as='textarea'
            rows={5}
            value={content}
            onChange={(e) => setContent(e.target.value)}
            style={{ borderLeft: '5px solid green' }}
          ></Form.Control>
        </Form.Group>

        <Button type='submit' className='btn btn-block btn-success'>
          <i class='fas fa-thumbs-up'></i> Create Post
        </Button>
      </Form>
    </FormContainer>
  );
};

export default CreatePostScreen;
