import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { listPostDetails } from '../redux/actions/post';
import { Link } from 'react-router-dom';
import {
  Col,
  Row,
  Image,
  ListGroup,
  Card,
  Button,
  Form,
} from 'react-bootstrap';
// import Rating from '../components/Rating';
// import Message from '../components/Message';
// import Loader from '../components/Loader';

const PostScreen = ({ match }) => {
  const dispatch = useDispatch();
  const postDetails = useSelector((state) => state.postDetails);
  const { loading, post, error } = postDetails;

  const addCommentHandler = () => {};

  useEffect(() => {
    dispatch(listPostDetails(match.params.id));
  }, [dispatch, match]);

  return (
    <>
      <Link className='btn btn-dark my-3' to='/'>
        Go Back
      </Link>

      {/* {loading ? (
        <Loader />
      ) : error ? (
        <Message variant='danger'>Product not found</Message>
      ) : ( */}
      <Row>
        <Col md={3}>
          <Image src='' alt='' fluid />
        </Col>

        <Col md={3}>
          <ListGroup variant='flush'>
            <ListGroup.Item>
              <h3>{post.title}</h3>
            </ListGroup.Item>

            <ListGroup.Item>Name : {post.name}</ListGroup.Item>
            <ListGroup.Item>Description : {post.content}</ListGroup.Item>
          </ListGroup>
        </Col>

        <Col md={6}>
          <h4>Comments</h4>
          <Card>
            <Card.Title as='div'>
              <strong> User : Username </strong>
            </Card.Title>
            <ListGroup variant='flush'>
              <ListGroup.Item>
                <Row>
                  <Col>
                    Comment : lorem ipsum dolor sit amet, consectetur adip
                  </Col>
                </Row>
              </ListGroup.Item>

              <ListGroup.Item>
                <Button
                  onClick={addCommentHandler}
                  className='btn-block'
                  type='button'
                  // disabled={product.countInStock === 0}
                >
                  Add a Comment
                </Button>
              </ListGroup.Item>
            </ListGroup>
          </Card>
        </Col>
      </Row>
      {/* )} */}
    </>
  );
};

export default PostScreen;
