import React from 'react';
import { Link } from 'react-router-dom';
import { Card, Row } from 'react-bootstrap';
const Post = ({ post }) => {
  const convertDate = (input) => {
    const date = new Date(input);
    return date.toDateString();
  };

  return (
    <>
      <Row>
        <p className='text-success'>
          posted on : {convertDate(post.createdAt)}
        </p>
      </Row>
      <Row>
        <Card className='my-3 py-3 rounded text-center'>
          {/* <Link to={`/posts/${post._id}`}>
        <Card.Img src={post.image} variant='top' className='img-fluid' />
      </Link> */}
          <Card.Body>
            <Link to={`/posts/${post._id}`}>
              <Card.Title as='h4'>
                <strong>{post.title} </strong>
              </Card.Title>
            </Link>
            <hr />
            <Card.Text as='h5'>{post.name}</Card.Text>
            <hr />
            <Card.Text as='p'>{post.content}</Card.Text>
          </Card.Body>
        </Card>
      </Row>
    </>
  );
};

export default Post;
